<?php$iriscrmKernelPath = dirname(dirname(dirname(__FILE__))) . '/vendor/iriscrm/source-core/src/Iris/';require_once $iriscrmKernelPath . 'core/engine/bootstrap.php';require_once Loader::getLoader()->loadOnce('common/Lib/lib.php');?><html><head><title>IRIS CRM - Пополнение баланса</title><meta content="text/html; charset=<?php echo GetDefaultEncoding(); ?>" http-equiv="Content-Type"/><link type="text/css" rel="stylesheet" href="balance.css"><script type="text/javascript" src="prototype-packed.js"> </script><script type="text/javascript">function is_decimal_correct(p_value) {	//if (Object.isUndefined(p_value) == true)	if (p_value == '')		return false;	if ((p_value.length > 0) && (p_value !='null') && (isNaN(p_value)))		return false;	else		return true;}function submitForm() {	var amount = $('balance_amount').value;	if (is_decimal_correct(amount) == false) {		alert("Введите корректное значение в поле суммы");		return;	}	var form = document.getElementsByTagName("form")[$('pay-table').getAttribute('num')];	try {		form.LMI_PAYMENT_AMOUNT.value = amount;	} catch (e) {}	try {		form.spAmount.value = amount;	} catch (e) {}	form.submit();	}function selectpay(p_this) {	var row = $(p_this.parentNode);	for (var i = 0; i < row.cells.length; i++) {		row.cells[i].removeClassName('t-button-selected');	}	$(p_this).addClassName('t-button-selected');	$('pay-table').setAttribute('num', $(p_this).getAttribute('num'));}</script></head><body><table width="100%" cellpadding="5" class="formtable">	<tbody>		<tr valign="middle">			<td align="center">				<table width="700" cellspacing="0" cellpadding="0" border="0">					<tbody>						<tr class="logo">							<td style="width: 100%;" colspan="2"><h1>Пополнение баланса</h1></td>						</tr>												<tr>							<td class="td-cell td-top"><span class="text">Введите сумму, на которую Вы хотите пополнить баланс</span></td>							<td class="td-cell td-top"><input style="width: 200px" id="balance_amount" type="text" value=""><span class="text" style="margin-left: 2px;">руб.</span></td>						</tr>						<tr>							<td id="pay-table" num="0" class="td-cell" colspan="2" align="center"><span class="text">Выберите платежную систему</span>								<table>									<tbody>										<tr>											<!--											<td num="0" class="t-button t-button-wm t-button-selected" onclick="selectpay(this)"><a class="i-link" href="wm/readme.html" target="_blank">Инструкция</a></td>											<td num="1" class="t-button t-button-sp" onclick="selectpay(this)" ><a class="i-link" href="sp/readme.html" target="_blank">Инструкция</a></td>											-->																						<td num="0" class="t-button t-button-wm t-button-selected" onclick="selectpay(this)"><div><a class="i-link" href="" href-r="wm/readme.html" target="_blank">Инструкция</a></div></td>											<td num="1" class="t-button t-button-sp" onclick="selectpay(this)" ><div><a class="i-link" href="" href-r="sp/readme.html" target="_blank">Инструкция</a></div></td>										</tr>									</tbody>								</table>							</td>						</tr>						<tr>							<td class="td-cell" colspan="2" style="text-align: center;"><input style="width: 200px;" type="button" onclick="submitForm();" value="Перейти к оплате"></td>						</tr>											</tbody>				</table>			</td>		</tr>	</tbody></table><?php$contact_id = $_GET['user_id'];$wm_form = file_get_contents("wm/payform.html");$sp_form = file_get_contents("sp/payform.html");$wm_form = iris_str_replace('#CONTACT_ID#', $contact_id, $wm_form);$sp_form = iris_str_replace('#CONTACT_ID#', $contact_id, $sp_form);$con = db_connect();$cmd = $con->prepare("select email from iris_contact where id=:id");$cmd->execute(array(":id" => $contact_id));$res = $cmd->fetchAll();$contact_email = $res[0][0];$sp_form = iris_str_replace('#EMAIL#', $contact_email, $sp_form);$sp_form = iris_str_replace('#sp_shop_id#', GetSystemVariableValue('sp_shop_id', $con), $sp_form);//$sp_form = iris_str_replace('#sp_confirm_url#', GetSystemVariableValue('sp_confirm_url', $con), $sp_form);//$sp_form = iris_str_replace('#sp_success_url#', GetSystemVariableValue('sp_success_url', $con), $sp_form);$wm_form = iris_str_replace('#wm_shop_id#', GetSystemVariableValue('wm_shop_id', $con), $wm_form);//$wm_form = iris_str_replace('#wm_confirm_url#', GetSystemVariableValue('wm_confirm_url', $con), $wm_form);//$wm_form = iris_str_replace('#wm_success_url#', GetSystemVariableValue('wm_success_url', $con), $wm_form);echo '<div style="display: none;">';echo $wm_form;echo $sp_form;echo '</div>';?></body></html>